import React from 'react';
import { useEffect , useState } from 'react';

const ScrollupButton = () => {

    const [top , setTop] = useState(false)

    useEffect(() => {
        window.addEventListener('scroll' , () => {
            if(window.scrollY > 100){
                setTop(true);
            }
            else{
                setTop(false);
            }
        })
    } , [])

    const ScrollUp = () => {
        window.scrollTo(0,0)
    }
    // const ScrollUp = () => {
    //     window.scrollTo({
    //         top:0,
    //         behavior:'smooth'
    //     })
    // }

  return (
    <div>
        {top && <button style={{
            position:'fixed',
            bottom:'50px',
            right:'50px',
            height:'50px',
            width:'50px',
            fontSize:'30px',
            backgroundColor:'rgb(113, 19, 207)',
        }} onClick={ScrollUp}
        >
        <i class="fa-solid fa-arrow-up" style={{col}}></i>
        </button>}

      
    </div>
  );
}

export default ScrollupButton;
